<span class="line"><span style="color:#C586C0;">        if</span><span style="color:#D4D4D4;"> (</span><span style="color:#9CDCFE;">tree</span><span style="color:#D4D4D4;"> == </span><span style="color:#569CD6;">null</span><span style="color:#D4D4D4;"> || </span><span style="color:#9CDCFE;">tree</span><span style="color:#D4D4D4;">[</span><span style="color:#569CD6;">nameof</span><span style="color:#D4D4D4;">(</span><span style="color:#569CD6;">this</span><span style="color:#D4D4D4;">.</span><span style="color:#9CDCFE;">Product</span><span style="color:#D4D4D4;">)] != </span><span style="color:#569CD6;">null</span><span style="color:#D4D4D4;">)</span></span>
